'use strict';

$(document).ready(function () {
  $('.popup-youtube').magnificPopup({
    disableOn: 700,
    type: 'iframe',
    mainClass: 'mfp-fade',
    removalDelay: 160,
    preloader: false,
    fixedContentPos: false
  });
  $('.slider-in-system').slick({
    dots: true,
    slidesToShow: 5,
    slidesToScroll: 5,
    appendArrows: $('.in-system-arrow'),
    appendDots: $('.in-system-dots'),
    infinite: false,
    responsive: [{
      breakpoint: 992,
      settings: {
        slidesToShow: 4,
        slidesToScroll: 4
      }
    }, {
      breakpoint: 600,
      settings: {
        slidesToShow: 3,
        slidesToScroll: 3
      }
    }, {
      breakpoint: 480,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 2
      }
    }]
  });
  $('.slider').slick({
    dots: true,
    appendDots: $('.dots-slider'),
    appendArrows: $('.arrows-slider'),
    infinite: false,
    adaptiveHeight: true
  });
  $(".mobile-menu").on("click", function () {
    $(this).toggleClass('open');
    $("header nav > ul").stop().slideToggle(function () {
      if ($(this).css('display') === 'none') {
        $(this).removeAttr('style');
      }
    });
  });

  $.fn.forceNumbericOnly = function () {
    return this.each(function () {
      $(this).keydown(function (e) {
        var key = e.charCode || e.keyCode || 0;
        return key == 8 || key == 9 || key == 46 || key >= 37 && key <= 40 || key >= 48 && key <= 57 || key >= 96 && key <= 105 || key == 107 || key == 109 || key == 173 || key == 61;
      });
    });
  };

  $('input[type=tel]').forceNumbericOnly();
});